INSERT INTO words (word, normalized_word, meaning, full_entry_text, etymology_type, variants, page, skeleton) VALUES ('1 uḏu:', '1 udu:', 'mound, heap; sand-dune', 'VU <b>1 uḏu:</b> pec. to <i>Kaş.</i> <b>Xak.</b> xı <b>uḏu:</b> <i>al-akama</i> ''mound, heap''; hence <i>al-kaṯib</i> ''a sand-dune'' is called <b>kum uḏu:</b>; and a town in <b>Arğu</b> is called <b>uḏu: Kent</b> <i>Kaş.</i> <i>I</i> 87; a.o. 110, 11 (<b>ıḍu:</b>).', 'VU', '["uḏu:"]', 42, 'DIS. ADA');
