INSERT INTO words (word, normalized_word, meaning, full_entry_text, etymology_type, variants, page, skeleton) VALUES ('1 yegü:', '1 yegü:', 'food', 'D <b>1 yegü:</b> Conc. N. fr. <b>yé:-</b>; ‘food’; n.o.a.b. <b>Xak.</b> xı <i>KB</i> (when a stranger comes, grant him lodging) <b>içür bér yégü</b> ‘give him drink and food’ 495; a.o. 4601 (<b>uvşa:k</b>): xııı (?) <i>Tef.</i> <b>yegü aş</b> ‘food to eat’ 149: xıv <i>Muh.</i>(?) (after <i>al-ţa''ām</i> <b>aş</b>); <i>al-ma''kūl</i> ‘edibles’ <b>yé:gü</b> Rif. 164 (only).', 'D', '[]', 911, 'DIS. YGE');
INSERT INTO words (word, normalized_word, meaning, full_entry_text, etymology_type, variants, page, skeleton) VALUES ('2 yegü:', '2 yegü:', '(uncertain)', '?E <b>2 yegü:</b> in <i>TT</i> <i>VIII</i> A.1 (see <b>1 bi:</b>) the translation of Sanskrit <i>kşurasya dhārā va</i> is damaged, but is read <b>y[e]g[ü]niŋ bisi</b> (MS. <b>pisi</b>) <b>teg</b>. The word to be expected here is <b>yüligüniŋ</b> and this should perhaps be restored.', 'Basic', '[]', 911, 'DIS. YGE');
INSERT INTO words (word, normalized_word, meaning, full_entry_text, etymology_type, variants, page, skeleton) VALUES ('yigi:', 'yigi:', 'close, compact, a seam', 'D <b>yigi:</b> Dev. N./A. fr. <b>*yig-</b>, cf. <b>yigtür-</b>; lit. ‘close, compact’; hence ‘a (tightly sewn) seam’. <i>Kaş.</i>’s phr. <b>yigi: yığa:ç</b> may be authentic, but the shorter form <b>yi: yığa:ç</b> is the common <b>Uyğ.</b> phr. <b>1: ığaç</b> ‘bushes and trees’, see <b>1 ı:</b>, and it may be no more than a mistaken reconstruction of that phr. The only certain survival is in NC <b>Kzx.</b> <b>jiyi</b> ‘thick, dense, frequent’; apparently also survives meaning ‘seam’ in NE Alt., Bar., Leb., Tel. <b>yik</b> (also ‘a cleft’, e.g. in the mountains) R <i>III</i> 507; Koib., Sag., Şor <b>çik</b> do. 2111; <b>Khak.</b> <b>çik</b>; Tuv. <b>tig</b>: NC <b>Kır.</b> <b>jik</b> (also ‘cleft’), and perhaps SE <b>Türki</b> <b>çek</b> BŞ. SW <b>Az.</b>, <b>Osm.</b> <b>yiv</b>, now ‘scratch, groove’, is the same word, see below. <b>Uyğ.</b> vııı ff. Civ. [gap] <b>yigi yekler bir[le]</b> ‘with a crowd of demons’ <i>TT</i> <i>VII</i> 28, 2: <b>Xak.</b> xı <b>yigi: yığa:ç</b> ‘closely packed (<i>al-kaţīru''l-multaff</i>) trees’; and the -g- is omitted from it and one says <b>yi:</b> (<b>?yı:</b>) <b>yığa:ç</b>; and one says <b>yigi: tı:ş</b> <i>al-sinnu''l-mutarāşş</i> ‘close set, compact teeth’; hence one says <b>to:n yigi: tik</b> ‘sew the garment and make its seams tight and strong’ (<i>ac''al darzahu mutarāşş muşamrac mu''akkad</i>); and one calls ‘a seam’ <b>yi:</b>, originally <b>yigi:</b> fr. this word <i>Kaş.</i> <i>III</i> 25; <b>yi:</b> ‘the seam of a garment’; hence ‘a tailor’ (<i>al-xayyāţ</i>) is called <b>yi:çi:</b>; <b>yi:</b> ‘a small cleft’ (<i>al-şi''b</i>) in the mountains; and one says <b>yi:</b> (<b>?yı:</b>) <b>yığa:ç</b> for ‘closely packed trees’, originally <b>yigi:</b> <i>mutarāşş</i>, but contracted; hence ‘close set teeth’ are called <b>yigi: tı:ş</b> <i>III</i> 216; <b>cigi: yi:</b> ‘a firmly sewn seam’, the c- is altered fr. y-, fr. the phr. <b>yigi:</b> (MS. <b>tigi:</b>) <b>tikti:</b> ‘he sewed firmly’. This is as we explained in the introduction to this book, the c- is substituted for y- in the language of the <b>Kıp.</b> and others, as in the word <b>cinçü:</b> originally w. y- <i>III</i> 229; a.o. <i>III</i> 283 (<b>sığza:-</b>): xıv <i>Muh.</i> <i>al-darz</i> <b>yi:g</b> Mel. 67, 1; <i>Rif.</i> 166 (<b>biyig</b>): <b>Osm.</b> xv ff. <b>yiv</b> c.i.a.p.; once (xv) definitely for ‘seam’, but generally translates <i>darz</i> in its broader meaning of ‘suture, wrinkle (on the face, etc.)’ TTS <i>I</i> 833; <i>II</i> 1056; <i>III</i> 813; <i>IV</i> 889.', 'D', '[]', 911, 'DIS. YGE');
INSERT INTO words (word, normalized_word, meaning, full_entry_text, etymology_type, variants, page, skeleton) VALUES ('yigit', 'yigit', 'a young man, strong and vigorous', '<b>yigit</b> usually ‘a young man, strong and vigorous’, also occasionally used for ‘young woman’; <i>Kaş.</i>’s translation prob. does not imply that it could be used for other than human beings. S.i.a.m.l.g. w. the usual phonetic changes, e.g. NE Alt. <b>yi:t</b> R <i>III</i> 523. <b>Uyğ.</b> vııı ff. Bud. (this old man was formerly) <b>yigit igsiz sizinteg kiçig körtle urı</b> ‘a vigorous, healthy, young, good-looking youth like you’ USp. 97, 10-12; o.o. U <i>III</i> 81, 3 etc. (<b>türk</b>): <b>Xak.</b> xı <b>yigit</b> <i>al-şābb min kull şay''</i> ‘a young man (from 16 to 30) of anything’ <i>Kaş.</i> <i>III</i> 8; o.o. <i>I</i> 353 (<b>türk</b>) and 9 others translated <i>al-şābb</i> or <i>al-fatā</i> ‘youth’: <i>KB</i> <b>ay edgü yigit</b> 359; (<b>Aytoldı</b>) <b>yigit erdi oğlan</b> ‘was a young man’ (good-tempered and intelligent) 463: xııı (?) <i>At.</i> <b>yigit koca bolur</b> ‘young men become old’ 193; <i>Tef.</i> <b>yigit</b> ‘a young man’ 153: xıv <i>Muh.</i> <i>al-şābb</i> (opposite to ‘old’ <b>karı:</b>) <b>yigi:t</b> Mel. 48, 15; <i>Rif.</i> 143; a.o. 152 (only): <b>Çağ.</b> xv ff. <b>yigit</b> (‘with -g-’) <i>cuwān</i> ‘young man’ <i>San.</i> 351r. 9 (quotn.): <b>Xwar.</b> xıv ditto <i>Qutb</i> 80; <i>MN</i> 96: <b>Kom.</b> xıv ‘young man’ <b>yigit</b> (or <b>igit</b>?) <i>CCI</i>; <b>yegit</b> <i>CCG</i>; <i>Gr.</i>: <b>Kıp.</b> xııı <i>al-şābb</i> <b>yeyit</b>, also pronounced <b>yigit</b> <i>Hou.</i> 24, 19: xıv <b>yigit</b> ditto <i>İd.</i> 96; <b>ye:’it</b> ditto, also called <b>yigit</b> do. 100: xv <i>şābb</i> <b>yigit</b> <i>Tuh.</i> 20b. 4: <b>Osm.</b> xıv ff. <b>yigit</b> (occasionally in xıv <b>igit</b>) (1) ‘a young man’; (2) as an Adj. ‘young’, also used to qualify ‘woman, moon, tree’, etc. TTS <i>I</i> 830; <i>II</i> 1053; <i>III</i> 810; <i>IV</i> 885.', 'Basic', '[]', 911, 'DIS. YGD');
INSERT INTO words (word, normalized_word, meaning, full_entry_text, etymology_type, variants, page, skeleton) VALUES ('yigde:', 'yigde:', 'the jujube tree', '<b>yigde:</b> originally ‘the jujube tree, Zizyphus angustifolia’ but in some modern languages ‘a species of oleaster, Elaeagnus angustifolia’. Survives in SE Tar., <b>Türki</b> <b>cigde:</b> NC <b>Kır.</b> <b>jigde/jiyde</b>; <b>Kzx.</b> <b>jide:</b> NW Kk. <b>jiyde:</b> SW <b>Az.</b> <b>iyde</b>; <b>Osm.</b>, <b>Tkm.</b> <b>igde</b>. The word is used both for the tree and its fruit. <b>Uyğ.</b> vııı ff. Civ. <b>yigde</b> H <i>II</i> 14, 128: <b>Xak.</b> xı <b>yigde:</b> <i>al-ğubayrā</i> ‘the jujube tree’ <i>Kaş.</i> <i>III</i> 31; a.o. <i>III</i> 147 (<b>ba:r</b>): xıv <i>Muh.</i> <i>al-ğubayrā</i> <b>yigde:</b> Mel. 78, 13; <i>Rif.</i> 183: <b>Oğuz</b> xı the Oğuz Türkmen pronounce <b>yigde:</b> <i>al-ğubayrā</i> as <b>yigte:</b> (-g- marked) <i>Kaş.</i> <i>I</i> 31, 25: <b>Kıp.</b> xııı <i>al-ğubayrā</i> <b>yigde:</b> <i>Hou.</i> 8, 8.', 'Basic', '[]', 911, 'DIS. YGD');
INSERT INTO words (word, normalized_word, meaning, full_entry_text, etymology_type, variants, page, skeleton) VALUES ('yögdik', 'yögdik', 'one of eight sporting birds', 'VU <b>yögdik</b> Hap. leg. <b>Xak.</b> xı <i>KB</i> 5377 one of eight sporting birds.', 'VU', '[]', 911, 'DIS. YGD');
INSERT INTO words (word, normalized_word, meaning, full_entry_text, etymology_type, variants, page, skeleton) VALUES ('yégeḏ-', 'yeged-', 'to get better, to succeed', 'D <b>yégeḏ-</b> Den. V. fr. <b>1 yé:g</b>; pec. to <b>Türkü</b> and <b>Uyğ.</b>; apparently both Intrans. ‘to get better, to succeed’ and Trans. ‘to make better, to improve’ and, at any rate in the Hend. <b>yégeḏ- ut-</b>, ‘to get the better of (someone)’. <b>Türkü</b> vııı [gap] <b>kılmadım</b> [<b>?elig</b>] <b>törü:g yégeḍi: kazğantım</b> ‘I did not make . . . I strove to improve and acquire the realm(?) and traditional law’ <i>II</i> E 36: vııı ff. Man.-A M <i>III</i> 6, 2-3 (iii) (<b>utlı:</b>): <b>Uyğ.</b> vııı ff. Man.-A (in a dating formula; at a good moment, on an auspicious day, in a blessed month) <b>yégeḍmiş kutluğ yılka</b> ‘in a successful and fortunate year’ M <i>I</i> 26, 23; o.o. do. 28, 18 etc. (<b>ut-</b>): Bud. (this Buddha is called . . . ‘having the voice of the sea’) <b>yértinçüde yégedmiş ünlüg</b> ‘having a voice which is successful in this world’ Kuan. 204-5; o.o. U <i>II</i> 21, 11-12 etc. (<b>ut-</b>); <i>TT</i> <i>VI</i> 419 (<b>ögüt-</b>): Civ. <b>asra atıŋ yégedtiŋ</b> ‘you have improved your low reputation’ <i>TT</i> <i>I</i> 7-8; o.o. do. 2 (<b>ut-</b>), 205 (<b>alkın-</b>).', 'D', '[]', 911, 'DIS. V. YGD-');
